	public function run ()
	{
		foreach ( $this->templatesData as $data ) {
			$template = new \App\Template();
			$template->template_name = $data[0];
			$template->show_header = $data[1];
			$template->repeated_fields = $data[2];
			$template->delimited_char = $data[3];
			$template->break_kits = $data[4];
			$template->is_active = $data[5];
			$template->save();
			$index = 1;
			foreach ( $data[6] as $option ) {
				$option_name = trim($option[1]);
				if ( empty( $option_name ) ) {
					continue;
				}
				$templateOption = new \App\TemplateOption();
				$templateOption->template_id = $template->id;
				$templateOption->line_item_field = $option[0];
				$templateOption->option_name = $option_name;
				$templateOption->option_category = $option[2];
				$templateOption->value = $option[3];
				$templateOption->width = $option[4];
				$templateOption->format = $option[5];
				$templateOption->template_order = $index;
				$index++;
				$templateOption->save();
			}
		}
	}
